const items = $input.all();

function tryParseWithHeals(s) {
  // Strip code fences
  let str = String(s).replace(/```json|```/g, '').trim();

  // Isolate JSON-ish slice
  const start = str.indexOf('{');
  const end = str.lastIndexOf('}');
  if (start === -1 || end === -1 || end <= start) return null;
  str = str.slice(start, end + 1);

  // First parse attempt
  try { return JSON.parse(str); } catch {}

  // Heal #1: remove stray closing brace before another key (your case)
  // e.g. {"post":"..."},"image_prompt":"..."}  ->  {"post":"...","image_prompt":"..."}
  let healed = str.replace(/}\s*,\s*(?="[^"]+"\s*:)/, ',');

  try { return JSON.parse(healed); } catch {}

  // Heal #2: remove trailing commas before closing braces
  healed = healed.replace(/,(\s*})/g, '$1');
  try { return JSON.parse(healed); } catch {}

  // Last-ditch: extract just "post" and optional prompt via regex so we still return something useful
  const postMatch = str.match(/"post"\s*:\s*"([\s\S]*?)"/);
  const promptMatch = str.match(/"image_prompt"\s*:\s*"([\s\S]*?)"/) || str.match(/"prompt"\s*:\s*"([\s\S]*?)"/);
  if (postMatch) {
    return {
      post: postMatch[1],
      image_prompt: promptMatch ? promptMatch[1] : '',
    };
  }
  return null;
}

return items.map((item) => {
  // Accept either {json: {output: "..."} } or {json: {output: {...}}}
  const out = item.json?.output;
  if (out == null) {
    return { json: { post: '⚠️ No valid JSON found in AI output.', prompt: '' } };
  }

  let obj = (typeof out === 'object') ? out : tryParseWithHeals(out);
  if (!obj) {
    return { json: { post: '⚠️ Error parsing AI output.', prompt: '' } };
  }

  // Extract fields
  let post = obj.post || '';
  let prompt = obj.image_prompt || obj.prompt || '';

  if (!post) {
    return { json: { post: '⚠️ Post missing from AI output.', prompt } };
  }

  // Normalize escaped newlines → real newlines
  post = String(post).replace(/\\n/g, '\n').trim();

  return { json: { post, prompt } };
});
